import styled from 'styled-components'
import { useState, useEffect } from 'react'
import {DebounceInput} from 'react-debounce-input'
import axios from 'axios'
import { Event } from '../../pages/e/event.types'
import EventThumbnail from '../event/EventThumbnail'
const SearchWrapper = styled.div`
`

const SearchParamButton = styled.div<{isSelected: boolean}>`
  display: grid;
  justify-content: center;
  place-items: center;
  font-weight: bold;
  border: 1px solid black;
  box-shadow: 0 0 0 0 black;
  background: ${(props) => props.isSelected ? 'blue' : 'none'};
`

const SearchInput = styled(DebounceInput)`
  width: 50%;
  border: 1px solid black;
`

const PageNumberButtonWrapper = styled.div`
  display: flex;
  justify-content: space-around;
`



const Search = () => {
  const [pageNo, selectPageNo] = useState(1)
  const [pageSize, setPageSize] = useState(2)
  const [searchQ, setSearchQ] = useState('')
  const [allEvents, setEvents] = useState<Event[]>([])
  const [isLoading, setLoading] = useState(false)
  const [isError, setError] = useState(false)
  const [eventsNo, setEventsNo] = useState(0)
  useEffect(() => {
    getEvents()
  }, [])
  useEffect(() =>
  {
    getEvents()
  }, [pageSize, pageNo, searchQ])

  const getEvents = async () => {
    setLoading(true)
    try {
      const res = await axios.get(`https://mock-api.drinks.test.siliconrhino.io/events?page=${pageNo}&pageSize=${pageSize}&search=${searchQ}`)
      const evnts: Event[] = res.data
      setEvents(evnts)
      setEventsNo(evnts.length + 1)
      setLoading(false)
    }
    catch (err) {
      setLoading(false)
      setError(true)
    }
  }

  if (isError) {
    return (
      <div>Failed to load data.</div>
    )
  }
  return (
    <SearchWrapper>
      <SearchInput
        minLength={1}
        debounceTimeout={300}
        placeholder={'asdf'}
        //@ts-ignore
        onChange={event => setSearchQ(event.target.value)}
      />
      <div>events: </div>
      {
        isLoading ?
        <div>loading...</div>
      :
        allEvents.map((event) => (
          <EventThumbnail
            id={event.id}
            time={event.time}
            title={event.title}
            creator={event.creator}
            guests={event.guests}
            type={event.type}
            location={event.location}
            comments={event.comments}
          />
        ))
      }
      <PageNumberButtonWrapper>
        {
          // lets say i got 8 events
          // how do I determine how many +5 will fit before we run out of events?
        }
        <div>Current page:</div>
        <SearchParamButton isSelected={pageNo === 1} onClick={() => selectPageNo(1)}>1</SearchParamButton>
        {eventsNo > pageSize && <SearchParamButton isSelected={pageNo === 2} onClick={() => selectPageNo(2)}>2</SearchParamButton>}
        {eventsNo > pageSize * 2 && <SearchParamButton isSelected={pageNo === 2} onClick={() => selectPageNo(2)}>2</SearchParamButton>}<SearchParamButton isSelected={pageNo === 3} onClick={() => selectPageNo(3)}>3</SearchParamButton>
        {eventsNo > pageSize * 4 && <SearchParamButton isSelected={pageNo === 2} onClick={() => selectPageNo(2)}>2</SearchParamButton>}<SearchParamButton isSelected={pageNo === 3} onClick={() => selectPageNo(3)}>3</SearchParamButton>
        <div>Items per page:</div>
        <SearchParamButton isSelected={pageSize === 5} onClick={() => setPageSize(5)}>5</SearchParamButton>
        <SearchParamButton isSelected={pageSize === 15} onClick={() => setPageSize(15)}>15</SearchParamButton>
        <SearchParamButton isSelected={pageSize === 22} onClick={() => setPageSize(25)}>25</SearchParamButton>
      </PageNumberButtonWrapper>
    </SearchWrapper>
  )
}

export default Search
