import styled from 'styled-components'
import { useState, useEffect } from 'react'
import {DebounceInput} from 'react-debounce-input'
import axios from 'axios'
import { Event } from '../../pages/e/event.types'
const SearchWrapper = styled.div`
`

const SearchParamButton = styled.div<{isSelected: boolean}>`
  display: grid;
  justify-content: center;
  place-items: center;
  font-weight: bold;
  border: 1px solid black;
  box-shadow: 0 0 0 0 black;
  background: ${(props) => props.isSelected ? 'blue' : 'none'};
`

const SearchInput = styled(DebounceInput)`
  width: 50%;
  border: 1px solid black;
`

const PageNumberButtonWrapper = styled.div`
  display: flex;
  justify-content: space-around;
`



const Search = () => {
  const [pageNo, selectPageNo] = useState(1)
  const [pageSize, setPageSize] = useState(10)
  const [searchQ, setSearchQ] = useState('')
  const [events, setEvents] = useState<Event[]>()
  const [isLoading, setLoading] = useState(false)
  useEffect(() => {
    getEvents()
  }, [])
  useEffect(() =>
  {
    getEvents()
  }, [pageSize, pageNo, searchQ])

  const getEvents = async () => {
    setLoading(true)
    const res = await axios.get(`https://mock-api.drinks.test.siliconrhino.io/events?page=${pageNo}&pageSize=${pageSize}&search=${searchQ}`)
    const events: Event[] = res.data
    setEvents(events)
    setLoading(false)
    return true
  }
  return (
    <SearchWrapper>
      <SearchInput
        minLength={1}
        debounceTimeout={300}
        placeholder={'asdf'}
        //@ts-ignore
        onChange={event => setSearchQ(event.target.value)}
      />
      <div>events: </div>
      {
        isLoading ?
        <div>loading...</div>
        :
        events && events.map((event) => {
          <div>{event.title}</div>
        })
      }
      <PageNumberButtonWrapper>
        <div>Current page:</div>
        <SearchParamButton isSelected={pageNo === 1} onClick={() => selectPageNo(1)}>1</SearchParamButton>
        <SearchParamButton isSelected={pageNo === 2} onClick={() => selectPageNo(2)}>2</SearchParamButton>
        <SearchParamButton isSelected={pageNo === 3} onClick={() => selectPageNo(3)}>3</SearchParamButton>
        <SearchParamButton isSelected={pageNo === 4} onClick={() => selectPageNo(4)}>4</SearchParamButton>
        <div>Items per page:</div>
        <SearchParamButton isSelected={pageSize === 10} onClick={() => setPageSize(10)}>10</SearchParamButton>
        <SearchParamButton isSelected={pageSize === 15} onClick={() => setPageSize(15)}>15</SearchParamButton>
        <SearchParamButton isSelected={pageSize === 20} onClick={() => setPageSize(20)}>20</SearchParamButton>
      </PageNumberButtonWrapper>
    </SearchWrapper>
  )
}

export default Search
